import React, { useEffect, useState } from "react";
import Logo from "../images/logo.png";

const Header = () => {
  const [isHeaderFixed, setIsHeaderFixed] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      // Ajoutez un délai de 0.5 seconde (500 millisecondes)
      setTimeout(() => {
        if (window.scrollY > 0) {
          setIsHeaderFixed(true);
        } else {
          setIsHeaderFixed(false);
        }
      }, 500);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const headerClassName = isHeaderFixed ? "navBar fixed-header" : "navBar";

  return (
    <div>
      <nav className={headerClassName}>
        <a href="#">
          <img src={Logo} alt="" className="logo" />
        </a>
        <div className="Nav-links">
          <ul>
            <li className="active">
              <a href="#">Accueil</a>
            </li>
            <li>
              <a href="#">Services</a>
            </li>
            <li>
              <a href="./A_propos.js">A propos</a>
            </li>
            <li>
              <a href="#">Avis</a>
            </li>
            <li>
              <a href="#">Contact</a>
            </li>
            <div className="contact_Header">
              <a href="#">
                contact@crea-tech.fr{" "}
                <p className="number_Header">06 42 14 03 29</p>
              </a>
            </div>
          </ul>
        </div>
      </nav>
    </div>
  );
};

export default Header;
